// 定义发送请求的总文件
import http from "@/utils/request";

// 定义请求登录数据接口
export const Login = (data) => {
    // 返回一个promise对象
    return http.post('/login', data)
}

// 获取左侧菜单
export const genMenuList = async () => {
    return await http.get('/menus')
}
// 获取用户数据列表
export const getUserList = (params) => {
    return http.get('/users', params)
}
